mapper.select:
select SId, Sname, Sage, Ssex from te_student


        and SId = #{sid}


        and Sname like '%${sname}%'


        and Ssex = #{ssex}

select sc.SId,tstu.Sname,sc.CId,tc.Cname,tt.tid,tt.Tname, t.avgscore from te_sc sc left join te_student tstu on tstu.SId = sc.SId left join te_course tc on tc.CId = sc.CId left join te_teacher tt on tt.TId = tc.TId left join (select sc.*, avg(sc.score) as avgscore from te_sc sc group by sc.SId) t on t.SId = sc.SId
and sc.SId = #{sid}
and sc.cid = #{cid}
and tt.tid = #{tid}
order by t.avgscore desc
